// Mapper les identifiants coingecko aux symboles standards
const idToSymbol = {
  'bitcoin': 'BTC',
  'ethereum': 'ETH',
  'solana': 'SOL',
  'cardano': 'ADA',
  'binancecoin': 'BNB',
  'polkadot': 'DOT',
  'ripple': 'XRP'
};

// Traiter les donn√©es de chaque crypto
return $json.map(coin => {
  // Calculer des indicateurs techniques basiques
  const price = coin.current_price;
  const volume = coin.total_volume;
  const market_cap = coin.market_cap;

  // Obtenir le symbole standardis√©
  const symbol = idToSymbol[coin.id] || coin.symbol.toUpperCase();

  // Calculer le ratio volume/market cap (indicateur de liquidit√©)
  const volume_to_market_cap = volume / market_cap;

  // Retourner l'objet format√© avec timestamp
  return {
    timestamp: new Date().toISOString(),
    id: coin.id,
    symbol: symbol,
    name: coin.name,
    price: price,
    market_cap: market_cap,
    total_volume: volume,
    price_change_1h: coin.price_change_percentage_1h_in_currency,
    price_change_24h: coin.price_change_percentage_24h_in_currency,
    price_change_7d: coin.price_change_percentage_7d_in_currency,
    volume_to_market_cap: volume_to_market_cap,
    circulating_supply: coin.circulating_supply,
    total_supply: coin.total_supply,
    max_supply: coin.max_supply,
    ath: coin.ath,
    ath_change_percentage: coin.ath_change_percentage,
    last_updated: coin.last_updated
  };
});

// Calculer des indicateurs techniques pour chaque crypto

const fs = require('fs');
const path = '/home/crypto_bot/data/';
const marketDataFile = path + 'market_data.json';

// Chargement des donn√©es historiques pour calculer les indicateurs
let historicalData = [];
try {
  if (fs.existsSync(marketDataFile)) {
    const content = fs.readFileSync(marketDataFile, 'utf8');
    historicalData = JSON.parse(content);
  }
} catch (error) {
  console.error('Erreur lors du chargement des donn√©es historiques:', error);
}

// Fonction pour calculer la moyenne mobile simple (SMA)
function calculateSMA(data, period) {
  if (data.length < period) return null;

  const sum = data.slice(0, period).reduce((acc, val) => acc + val, 0);
  return sum / period;
}

// Fonction pour calculer le RSI (Relative Strength Index)
function calculateRSI(prices, period = 14) {
  if (prices.length < period + 1) return null;

  const deltas = [];
  for (let i = 1; i < prices.length; i++) {
    deltas.push(prices[i] - prices[i - 1]);
  }

  const gains = deltas.map(d => d > 0 ? d : 0);
  const losses = deltas.map(d => d < 0 ? Math.abs(d) : 0);

  // Calculer la moyenne des gains et des pertes
  let avgGain = gains.slice(0, period).reduce((acc, val) => acc + val, 0) / period;
  let avgLoss = losses.slice(0, period).reduce((acc, val) => acc + val, 0) / period;

  if (avgLoss === 0) return 100;

  // Calculer le RS et le RSI
  const RS = avgGain / avgLoss;
  const RSI = 100 - (100 / (1 + RS));

  return RSI;
}

// R√©cup√©rer les nouvelles donn√©es
const newData = $json;

// Enrichir avec les indicateurs techniques
const enrichedData = newData.map(coin => {
  // R√©cup√©rer l'historique de cette crypto
  const coinHistory = historicalData.filter(entry => entry.symbol === coin.symbol)
    .sort((a, b) => new Date(b.timestamp) - new Date(a.timestamp));

  // Ajouter les donn√©es actuelles pour les calculs
  const allPrices = [coin.price, ...coinHistory.map(entry => entry.price)];

  // Calculer les indicateurs
  const sma20 = calculateSMA(allPrices, 20);
  const sma50 = calculateSMA(allPrices, 50);
  const rsi14 = calculateRSI(allPrices, 14);

  // Calculer les signaux techniques
  const priceAboveSMA20 = sma20 ? coin.price > sma20 : null;
  const priceAboveSMA50 = sma50 ? coin.price > sma50 : null;

  // Interpr√©tation du RSI
  let rsiSignal = 'NEUTRAL';
  if (rsi14 !== null) {
    if (rsi14 > 70) rsiSignal = 'OVERBOUGHT';
    else if (rsi14 < 30) rsiSignal = 'OVERSOLD';
  }

  // Combiner les signaux pour une √©valuation globale
  let technicalSignal = 'NEUTRAL';

  if (priceAboveSMA20 && priceAboveSMA50) {
    technicalSignal = 'BULLISH';
  } else if (!priceAboveSMA20 && !priceAboveSMA50) {
    technicalSignal = 'BEARISH';
  }

  // Ajuster selon le RSI
  if (rsiSignal === 'OVERBOUGHT' && technicalSignal === 'BULLISH') {
    technicalSignal = 'BULLISH_CAUTION'; // Tendance haussi√®re mais potentiellement en surachat
  } else if (rsiSignal === 'OVERSOLD' && technicalSignal === 'BEARISH') {
    technicalSignal = 'BEARISH_OPPORTUNITY'; // Tendance baissi√®re mais potentielle opportunit√©
  }

  return {
    ...coin,
    technical_indicators: {
      sma20: sma20,
      sma50: sma50,
      rsi14: rsi14,
      price_above_sma20: priceAboveSMA20,
      price_above_sma50: priceAboveSMA50,
      rsi_signal: rsiSignal
    },
    technical_signal: technicalSignal
  };
});

// Fusionner avec les donn√©es existantes
const mergedData = [...enrichedData, ...historicalData];

// Limiter √† 20000 entr√©es max (cela correspond √† environ 2-3 semaines de donn√©es)
const limitedData = mergedData.slice(0, 20000);

// Sauvegarder les donn√©es
try {
  fs.writeFileSync(marketDataFile, JSON.stringify(limitedData, null, 2));

  console.log(`Donn√©es de march√© mises √† jour: ${enrichedData.length} nouvelles entr√©es, ${limitedData.length} entr√©es au total`);
} catch (error) {
  console.error('Erreur lors de la sauvegarde des donn√©es:', error);
}

// Retourner les statistiques et un r√©sum√© des signaux
const signalSummary = {};
enrichedData.forEach(coin => {
  signalSummary[coin.symbol] = {
    price: coin.price,
    change_1h: coin.price_change_1h,
    change_24h: coin.price_change_24h,
    technical_signal: coin.technical_signal,
    rsi: coin.technical_indicators.rsi14
  };
});

return {
  success: true,
  timestamp: new Date().toISOString(),
  crypto_count: enrichedData.length,
  signals: signalSummary
};

// Sauvegarder l'indice Fear & Greed

const fs = require('fs');
const path = '/home/crypto_bot/data/';
const fearGreedFile = path + 'fear_greed_index.json';

// Obtenir les donn√©es Fear & Greed
let fearGreedData = {
  value: 0,
  value_classification: 'Neutral',
  timestamp: new Date().toISOString()
};

try {
  if ($json.data && $json.data[0]) {
    fearGreedData = {
      value: parseInt($json.data[0].value) || 0,
      value_classification: $json.data[0].value_classification || 'Neutral',
      timestamp: new Date().toISOString()
    };
  }
} catch (error) {
  console.error('Erreur lors du traitement des donn√©es Fear & Greed:', error);
}

// Charger l'historique existant
let historicalFG = [];
try {
  if (fs.existsSync(fearGreedFile)) {
    const content = fs.readFileSync(fearGreedFile, 'utf8');
    historicalFG = JSON.parse(content);
  }
} catch (error) {
  console.error('Erreur lors du chargement des donn√©es Fear & Greed:', error);
}

// Ajouter la nouvelle entr√©e
historicalFG.unshift(fearGreedData);

// Limiter √† 365 entr√©es max (environ 1 an)
const limitedFG = historicalFG.slice(0, 365);

// Sauvegarder les donn√©es
try {
  fs.writeFileSync(fearGreedFile, JSON.stringify(limitedFG, null, 2));

  console.log(`Fear & Greed Index mis √† jour: ${fearGreedData.value} (${fearGreedData.value_classification})`);
} catch (error) {
  console.error('Erreur lors de la sauvegarde des donn√©es Fear & Greed:', error);
}

// Retourner les informations Fear & Greed
return {
  success: true,
  fear_greed_index: fearGreedData,
  history_count: limitedFG.length
};

// G√©n√©rer un rapport quotidien du march√©

const fs = require('fs');
const path = '/home/crypto_bot/data/';
const reportFile = path + 'market_reports.json';

// Charger les rapports existants
let reports = [];
try {
  if (fs.existsSync(reportFile)) {
    const content = fs.readFileSync(reportFile, 'utf8');
    reports = JSON.parse(content);
  }
} catch (error) {
  console.error('Erreur lors du chargement des rapports:', error);
}

// V√©rifier si un rapport a d√©j√† √©t√© g√©n√©r√© aujourd'hui
const today = new Date();
const todayStr = today.toISOString().split('T')[0];

const existingReport = reports.find(report => 
  report.date && report.date.startsWith(todayStr)
);

// Si un rapport existe d√©j√†, ne pas en cr√©er un nouveau
if (existingReport) {
  return {
    success: true,
    message: 'Un rapport a d√©j√† √©t√© g√©n√©r√© aujourd\'hui',
    existing_report: existingReport
  };
}

// Charger les donn√©es de march√© et Fear & Greed
let marketData = [];
let fearGreedData = [];
try {
  const marketContent = fs.readFileSync(path + 'market_data.json', 'utf8');
  marketData = JSON.parse(marketContent);

  const fgContent = fs.readFileSync(path + 'fear_greed_index.json', 'utf8');
  fearGreedData = JSON.parse(fgContent);
} catch (error) {
  console.error('Erreur lors du chargement des donn√©es pour le rapport:', error);
}

// Organiser les donn√©es par crypto
const cryptoData = {};
const symbols = ['BTC', 'ETH', 'SOL', 'ADA', 'BNB', 'DOT', 'XRP'];

symbols.forEach(symbol => {
  const coinData = marketData.filter(entry => entry.symbol === symbol)
    .sort((a, b) => new Date(b.timestamp) - new Date(a.timestamp));

  if (coinData.length > 0) {
    const latest = coinData[0];
    const oneDayAgo = coinData.find(entry => {
      const entryTime = new Date(entry.timestamp);
      const diff = today - entryTime;
      return diff >= 24 * 60 * 60 * 1000; // Au moins 24h d'√©cart
    });

    const oneWeekAgo = coinData.find(entry => {
      const entryTime = new Date(entry.timestamp);
      const diff = today - entryTime;
      return diff >= 7 * 24 * 60 * 60 * 1000; // Au moins 7 jours d'√©cart
    });

    cryptoData[symbol] = {
      current_price: latest.price,
      current_signal: latest.technical_signal,
      price_change_24h: oneDayAgo ? ((latest.price - oneDayAgo.price) / oneDayAgo.price * 100) : latest.price_change_24h,
      price_change_7d: oneWeekAgo ? ((latest.price - oneWeekAgo.price) / oneWeekAgo.price * 100) : latest.price_change_7d,
      volume_24h: latest.total_volume,
      rsi: latest.technical_indicators?.rsi14 || null
    };
  }
});

// Obtenir le Fear & Greed actuel
const currentFG = fearGreedData.length > 0 ? fearGreedData[0] : null;

// Cr√©er le rapport
const report = {
  date: today.toISOString(),
  market_summary: {
    btc_dominance: marketData.length > 0 ? 
      (marketData.find(entry => entry.symbol === 'BTC')?.market_cap / 
      marketData.reduce((sum, entry) => sum + entry.market_cap, 0) * 100) : null,
    total_market_cap: marketData.reduce((sum, entry) => sum + entry.market_cap, 0),
    fear_greed_index: currentFG ? currentFG.value : null,
    fear_greed_label: currentFG ? currentFG.value_classification : 'Neutral',
  },
  crypto_data: cryptoData,
  market_trends: {
    bullish_count: Object.values(cryptoData).filter(data => 
      data.current_signal === 'BULLISH' || data.current_signal === 'BULLISH_CAUTION'
    ).length,
    bearish_count: Object.values(cryptoData).filter(data => 
      data.current_signal === 'BEARISH' || data.current_signal === 'BEARISH_OPPORTUNITY'
    ).length,
    neutral_count: Object.values(cryptoData).filter(data => 
      data.current_signal === 'NEUTRAL'
    ).length,
    overall_market_sentiment: 'NEUTRAL'
  }
};

// D√©terminer le sentiment global du march√©
if (report.market_trends.bullish_count > report.market_trends.bearish_count + report.market_trends.neutral_count) {
  report.market_trends.overall_market_sentiment = 'BULLISH';
} else if (report.market_trends.bearish_count > report.market_trends.bullish_count + report.market_trends.neutral_count) {
  report.market_trends.overall_market_sentiment = 'BEARISH';
} else if (currentFG && currentFG.value < 25) {
  report.market_trends.overall_market_sentiment = 'EXTREME_FEAR';
} else if (currentFG && currentFG.value > 75) {
  report.market_trends.overall_market_sentiment = 'EXTREME_GREED';
}

// Ajouter le rapport √† la liste et sauvegarder
reports.unshift(report);

// Garder seulement les 365 derniers rapports
const limitedReports = reports.slice(0, 365);

// Sauvegarder les rapports
try {
  fs.writeFileSync(reportFile, JSON.stringify(limitedReports, null, 2));

  console.log(`Rapport de march√© g√©n√©r√© pour ${todayStr}`);
} catch (error) {
  console.error('Erreur lors de la sauvegarde du rapport:', error);
}

return {
  success: true,
  report: report,
  report_date: todayStr
};

