// Extraire les vid√©os pertinentes
const videos = $json.items || [];

if (!videos.length) {
  return [];
}

// Charger les vid√©os d√©j√† trait√©es
const fs = require('fs');
const path = '/home/crypto_bot/data/';
const processedVideosFile = path + 'processed_videos.json';

let processedVideos = [];
try {
  if (fs.existsSync(processedVideosFile)) {
    const content = fs.readFileSync(processedVideosFile, 'utf8');
    processedVideos = JSON.parse(content);
  }
} catch (error) {
  console.error('Erreur lors du chargement des vid√©os trait√©es:', error);
}

// Filtrer les vid√©os d√©j√† trait√©es
const processedIds = new Set(processedVideos.map(v => v.id));
const newVideos = videos.filter(video => !processedIds.has(video.id.videoId));

console.log(`Trouv√© ${videos.length} vid√©os, dont ${newVideos.length} nouvelles`);

// Formater les nouvelles vid√©os
return newVideos.map(video => {
  // D√©terminer les cryptos mentionn√©es
  const title = video.snippet.title.toLowerCase();
  const description = video.snippet.description.toLowerCase();
  const content = title + ' ' + description;

  const relatedCrypto = [];

  if (content.includes('bitcoin') || content.includes('btc')) relatedCrypto.push('BTC');
  if (content.includes('ethereum') || content.includes('eth')) relatedCrypto.push('ETH');
  if (content.includes('solana') || content.includes('sol')) relatedCrypto.push('SOL');
  if (content.includes('cardano') || content.includes('ada')) relatedCrypto.push('ADA');
  if (content.includes('binance') || content.includes('bnb')) relatedCrypto.push('BNB');
  if (content.includes('polkadot') || content.includes('dot')) relatedCrypto.push('DOT');
  if (content.includes('ripple') || content.includes('xrp')) relatedCrypto.push('XRP');

  // Si aucune crypto sp√©cifique n'est mentionn√©e mais que "crypto" est mentionn√©,
  // consid√©rer comme g√©n√©ral
  if (relatedCrypto.length === 0 && content.includes('crypto')) {
    relatedCrypto.push('GENERAL');
  }

  return {
    id: video.id.videoId,
    title: video.snippet.title,
    description: video.snippet.description,
    channel_id: video.snippet.channelId,
    channel_title: video.snippet.channelTitle,
    published_at: video.snippet.publishedAt,
    thumbnail: video.snippet.thumbnails.high.url,
    related_crypto: relatedCrypto,
    url: `https://www.youtube.com/watch?v=${video.id.videoId}`,
    processed: false,
    timestamp: new Date().toISOString()
  };
});

// Traiter la r√©ponse de Claude
const video = $json;
let analysis = {};

try {
  // Extraire le contenu JSON de la r√©ponse
  const claudeResponse = $node["Analyser Contenu"].json;
  const content = claudeResponse.content[0].text;

  // Essayer de parser le JSON
  try {
    // Trouver le JSON dans la r√©ponse
    const jsonMatch = content.match(/\{[\s\S]*\}/);
    if (jsonMatch) {
      analysis = JSON.parse(jsonMatch[0]);
    } else {
      throw new Error("Format JSON non trouv√© dans la r√©ponse");
    }
  } catch (parseError) {
    console.error("Erreur lors du parsing JSON:", parseError);
    // Cr√©er un objet d'analyse par d√©faut
    analysis = {
      summary: content.substring(0, 200) + "...",
      key_points: ["Analyse non structur√©e disponible"],
      price_predictions: [],
      investment_advice: "Aucun conseil d'investissement structur√© disponible",
      sentiment: "neutral",
      confidence_score: 30
    };
  }

  // Marquer la vid√©o comme trait√©e
  video.processed = true;
  video.analysis = analysis;

  // Sauvegarder dans la liste des vid√©os trait√©es
  const fs = require('fs');
  const path = '/home/crypto_bot/data/';
  const processedVideosFile = path + 'processed_videos.json';
  const videoAnalysisFile = path + 'video_analysis.json';

  // Charger les vid√©os d√©j√† trait√©es
  let processedVideos = [];
  try {
    if (fs.existsSync(processedVideosFile)) {
      const content = fs.readFileSync(processedVideosFile, 'utf8');
      processedVideos = JSON.parse(content);
    }
  } catch (error) {
    console.error('Erreur lors du chargement des vid√©os trait√©es:', error);
  }

  // Ajouter la nouvelle vid√©o
  processedVideos.push({
    id: video.id,
    title: video.title,
    url: video.url,
    processed_at: new Date().toISOString()
  });

  // Limiter √† 1000 vid√©os max
  const limitedVideos = processedVideos.slice(0, 1000);

  // Sauvegarder la liste des vid√©os trait√©es
  fs.writeFileSync(processedVideosFile, JSON.stringify(limitedVideos, null, 2));

  // Charger les analyses existantes
  let videoAnalyses = [];
  try {
    if (fs.existsSync(videoAnalysisFile)) {
      const content = fs.readFileSync(videoAnalysisFile, 'utf8');
      videoAnalyses = JSON.parse(content);
    }
  } catch (error) {
    console.error('Erreur lors du chargement des analyses vid√©o:', error);
  }

  // Ajouter la nouvelle analyse
  videoAnalyses.unshift(video);

  // Limiter √† 1000 analyses max
  const limitedAnalyses = videoAnalyses.slice(0, 1000);

  // Sauvegarder les analyses
  fs.writeFileSync(videoAnalysisFile, JSON.stringify(limitedAnalyses, null, 2));

  console.log(`Vid√©o analys√©e et sauvegard√©e: ${video.title}`);

  return video;
} catch (error) {
  console.error('Erreur lors du traitement de l\'analyse:', error);
  return {
    ...video,
    error: error.message
  };
}

